import React, {useState} from 'react';
import './App.css';
import {TasksType, TodoList} from "./Components/TodoList";

export type TaskValueType = "All" | "Active" | "Completed";

function App() {
    let [tasks, setTasks] = useState<Array<TasksType>>(
        [
            {id: 1, title: "HTML&CSS", isDone: true},
            {id: 2, title: "JS", isDone: true},
            {id: 3, title: "ReactJS", isDone: false},
            {id: 4, title: "JS", isDone: true}
        ]
    )
    const removeTask = (taskID: number) => {
        setTasks(tasks.filter(task => task.id !== taskID));
    }

    // !filter
    let [filter, setFilter] = useState<TaskValueType>("All"); // use state
    let filteredTasks = tasks; // отфильтрованные таски на основе всех тасков
    if (filter === "Completed") {
        filteredTasks = tasks.filter(task => task.isDone) // условие
    }
    if (filter === "Active") {
        filteredTasks = tasks.filter(task => !task.isDone) // условие
    }
    // !filter 

    // передача функций
    return (
        <div className='App'>
            <TodoList
                title='What to learn'
                tasks={filteredTasks}
                removeTask={removeTask}
            />
        </div>
    );
}

export default App;
